mapper.update:
UPDATE car SET carBrand=#{car.carBrand}
             , carModel=#{car.carModel}
             , carStruct=#{car.carStruct}
             , carDisplacement=#{car.carDisplacement}
             , isManual=#{car.isManual}
             , maxMum=#{car.maxMum}
             , originalPrice=#{car.originalPrice}
             , discount=#{car.discount}
            WHERE
            carID=#{car.carID};

mapper.insert:
INSERT INTO car(carID
              , carBrand
              , carModel
              , carStruct
              , carDisplacement
              , isManual
              , maxMum
              , originalPrice
              , discount)
              VALUES (
                  #{car.carID}
                , #{car.carBrand}
                , #{car.carModel}
                , #{car.carStruct}
                , #{car.carDisplacement}
                , #{car.isManual}
                , #{car.maxMum}
                , #{car.originalPrice}
                , #{car.discount}
                )
